(1437, 237, '_elementor_data', '[{\"id\":\"15e0430\",\"elType\":\"section\",\"settings\":{\"margin\":{\"unit\":\"px\",\"top\":\"-80\",\"right\":0,\"bottom\":\"0\",\"left\":0,\"isLinked\":false}},\"elements\":[{\"id\":\"d361bfc\",\"elType\":\"column\",\"settings\":{\"_column_size\":100,\"_inline_size\":null},\"elements\":[{\"id\":\"91abdea\",\"elType\":\"widget\",\"settings\":{\"title\":\"Sele\\u00e7\\u00e3o Brasileira se classifica para o pr\\u00e9-ol\\u00edmpico\",\"align\":\"center\",\"title_color\":\"#000000\",\"typography_typography\":\"custom\",\"typography_font_family\":\"Roboto\",\"text_shadow_text_shadow_type\":\"yes\",\"text_shadow_text_shadow\":{\"horizontal\":-5,\"vertical\":0,\"blur\":0,\"color\":\"#F3F702\"}},\"elements\":[],\"widgetType\":\"heading\"}],\"isInner\":false}],\"isInner\":false},{\"id\":\"8146043\",\"elType\":\"section\",\"settings\":{\"margin\":{\"unit\":\"px\",\"top\":\"-117\",\"right\":0,\"bottom\":\"0\",\"left\":0,\"isLinked\":false}},\"elements\":[{\"id\":\"c15831e\",\"elType\":\"column\",\"settings\":{\"_column_size\":100,\"_inline_size\":null},\"elements\":[{\"id\":\"030c968\",\"elType\":\"widget\",\"settings\":{\"image\":{\"url\":\"https:\\/\\/handbolh1.000webhostapp.com\\/wp-content\\/uploads\\/2020\\/04\\/Selecao-de-handebol.jpg\",\"id\":239}},\"elements\":[],\"widgetType\":\"image\"}],\"isInner\":false}],\"isInner\":false},{\"id\":\"6be7c26\",\"elType\":\"section\",\"settings\":{\"margin\":{\"unit\":\"px\",\"top\":\"-125\",\"right\":0,\"bottom\":\"0\",\"left\":0,\"isLinked\":false}},\"elements\":[{\"id\":\"a6a594c\",\"elType\":\"column\",\"settings\":{\"_column_size\":100,\"_inline_size\":null},\"elements\":[{\"id\":\"b89f60e\",\"elType\":\"widget\",\"settings\":{\"editor\":\"<div class=\\\"mc-column content-text active-extra-styles \\\" data-block-type=\\\"unstyled\\\" data-block-weight=\\\"50\\\" data-block-id=\\\"5\\\"><p class=\\\"content-text__container \\\" data-track-category=\\\"Link no Texto\\\" data-track-links=\\\"\\\"